import { useState, useEffect } from 'react';
//import Searchbar from "./Searchbar/Searchbar";
import { fetchTrendingToday, fetchSearchMovies } from 'api';
//import { Loader } from "../components/Loader/Loader";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import { Route, Routes } from "react-router-dom";
import { Home } from "../pages/Home";
import { Container, Header, Logo, Link } from "./App.styled";
import Movies from '../pages/Movies'
import Movie from '../pages/Movie'

export default function App() {
  const [trends, setTrends] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [query, setQuery] = useState('');
  const [foundMovies, setFoundMovies] = useState([])

  const url = {
    trending: 'trending/movie/day',
    search: 'search/movie'
    // / movie / { movie_id }
    // / movie / { movie_id } / credits
    // / movie / { movie_id } / reviews
  }


  useEffect(() => {
    // if (search === "") {

    //   return;
    // }
    async function fetchTrending() {
      try {
        setIsLoading(true);
        const data = await fetchTrendingToday(url.trending);
        const arrTrends = data.results.map(({ id, title, backdrop_path, poster_path
        }) => {
          return {
            id,
            title,
            backdrop_path,
            poster_path,
          };
        });

        if (arrTrends) {
          setTrends(arrTrends);
        }


        // if (imgs.length) {
        //   setImages(images => [...images, ...imgs]);
        //   setisBtn(true);
        // }
        // if (imgs.length === 0) {
        //   toast('По вашему запросу картинок не найдено');
        //   setIsLoading(false);
        //   setisBtn(false);
        //   return
        // }
        // if (imgs.length < 12) {
        //   setIsLoading(false);
        //   toast('Картинок больше нет');
        //   setisBtn(false);
        //   return
        // }
        // if (page === 1) {
        //   toast(` Мы нашли  ${data.totalHits} картинок.`);
        // }

      } catch (error) {
        toast(error.massage)
      } finally {
        setIsLoading(false);
      }
    }
    fetchTrending()

  }, [url.trending])

  useEffect(() => {
    if (query === "") {
      return;
    }
    async function fetchSearch() {
      try {
        setIsLoading(true);
        const data = await fetchSearchMovies(url.search, query);
        const foundMovies = data.results.map(({ id, title, backdrop_path, poster_path
        }) => {
          return {
            id,
            title,
            backdrop_path,
            poster_path,
          };
        });

        if (foundMovies) {
          setFoundMovies(foundMovies);
        }
      } catch (error) {
        toast(error.massage)
      } finally {
        setIsLoading(false);
      }
    }
    fetchSearch()

  }, [query, url.search])

  // const toggleModal = () => {
  //   setShowModal(!showModal);
  // }

  const getQueryValue = (value) => {
    if (value === "") {
      setQuery(value)
      // setPage(1);
      toast('Впишите значение поиска')
      return;
    }
    setQuery(value);
    //setFoundMovies([])


  }
  return (
    // <Wrapper>
    //   <ToastContainer autoClose={1000} />
    //   <Searchbar onSearch={getVisiblImages} />
    //   {isLoading && <Loader />}
    // </Wrapper>
    <Container>
      <ToastContainer autoClose={1000} />
      <Header>
        {/* <Logo>
          <span role="img" aria-label="computer icon">
            💻
          </span>{" "}
          GoMerch Store
        </Logo> */}
        <nav>
          <Link to="/" end>
            Home
          </Link>
          <Link to="/movies">Movies</Link>
        </nav>
      </Header>
      <Routes>
        <Route path="/" element={<Home trends={trends} />} />
        <Route path="/movies" element={<Movies onSearch={getQueryValue} foundMovies={foundMovies} />} />
        <Route path="/movies/:movieID" element={<Movie />} >
          <Route path="/" element={<div>Cost</div>} />
        </Route>
      </Routes>
    </Container>
  );
}
