import axios from 'axios';

axios.defaults.baseURL = 'https://pixabay.com/api/';

export const fetchImages = async () => {
	const response = await axios.get('', {
		params: {
			key: '30718991-276e8f59eb65e473510905548',
			per_page: 12,
		}
	})
	return response.data;
};





