	const handleClick = () => {
		setVisibility(prevvisibility => !prevvisibility)
	}

	const openAnimation = {
		x: 0,
		transition: {
			duration: 200
		}
	};
	return (
		<div
			className="wrapper"
			open={visibility}
		>
			<button onClick={handleClick} style={{ right: 10 }}>
				x
			</button>
			<div className="logo">ROC</div>
