/* eslint-disable no-useless-concat */
import React from "react";
import Searchbar from "./Searchbar/Searchbar";
import { Component } from 'react';
import { fetchImages } from 'api';
import { ImageGallary } from './ImageGallery/ImageGallary';
import { Wrapper } from "./App.styled";
import { ButtonLoadMore } from '../components/Button/Button';
import { Loader } from "../components/Loader/Loader";
import Modal from "./Modal/Modal";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';




export default class App extends Component {
  state = {
    images: [],
    error: '',
    isLoading: false,
    showModal: false,
    search: '',
    page: 1,
    activImg: '',
    total: 0,
    tags:''
  };

  // async componentDidMount() {
  //   try {
  //     this.setState({ isLoading: true });
  //     const images = await fetchImages();
  //     this.setState({
  //       images: images.hits,
  //       isLoading: false,
  //     });
  //   } catch (error) {
  //     this.setState({
  //       error:
  //         'Мы не смогли загрузить картинки, пожалуйста перезагрузите страницу чтобы попробовать еще раз 🥹',
  //     });
  //   }
  // }

  async componentDidUpdate(_, prevState) {
    const { page, search } = this.state
    if (prevState.page !== page){
      try {
        this.setState({ isLoading: true });
        const nextImages = await fetchImages(search, page);
        const data = nextImages.hits.map(({ id, webformatURL, largeImageURL, tags }) => {
          return {
            id,
            webformatURL,
            largeImageURL,
            tags,
          };
        });

    this.setState(prevState => ({
      images: [...prevState.images, ...data],
      isLoading: false,
    }))

      } catch (error) {
        toast(error.massage)
      } 
      this.scrollOnLoadButton()
    }
    if(prevState.search !== search){
      try {
        this.setState({images:[]})
        this.setState({ isLoading: true });
        const nextImages = await fetchImages(search, page);
        const data = nextImages.hits.map(({ id, webformatURL, largeImageURL, tags }) => {
          return {
            id,
            webformatURL,
            largeImageURL,
            tags,
          };
        });
        this.setState(prevState => ({
          images: [...prevState.images, ...data],
          isLoading: false,
        }))
      } catch (error) {
        toast(error.massage)
      }
    }

  }


  toggleModal = () => {
    this.setState(({ showModal }) => ({
      showModal: !showModal,
    }))
  }
  getVisiblImages = (value) => {
    if (value === ""){
      this.setState({images:[]})
			toast('Впишите значение поиска')
      return;
	}
    this.setState({
      search: value
    })
  }
  handleModal = (url) => {
    this.setState({
      activImg: url
    })
  }
  loadMore = () => {
    this.setState(prevState => ({
      page: prevState.page + 1
    }))
  }
  scrollOnLoadButton = () => {
    window.scrollTo({
      top: document.documentElement.scrollHeight,
      behavior: 'smooth',
    });
  };

  render() {

    console.log(this.state.images.length)
    const { showModal, isLoading, error, images } = this.state;

    return (
      <Wrapper>
        <ToastContainer/>
        {error && <p>{error}</p>}
        <Searchbar onSearch={this.getVisiblImages} />
        {showModal && (
          <Modal onClose={this.toggleModal}>
            <img src={this.state.activImg} alt="" />
          </Modal>
        )}
        {isLoading ? <Loader /> : <ImageGallary images={images} onSwitchModal={this.handleModal} onModal={this.toggleModal} />}
        {images.length > 0 && <ButtonLoadMore onLoadMore={this.loadMore} onScroll={this.scrollOnLoadButton}>Load more</ButtonLoadMore>}
      </Wrapper>
    );
  }
}

