import { useState } from "react";

import { FaHome, FaYoutube, FaProductHunt } from 'react-icons/fa';
import { NavLink } from "react-router-dom";

import "./Basic.css";





export const Burger = ({ open }) => {
	const [visibility, setVisibility] = useState(false)

	const handleClick = () => {
		setVisibility(prevvisibility => !prevvisibility)
	}


	return (
		<div
			className="wrapper"
			open={visibility}
		>
			<button className="dropdown" onClick={handleClick} style={{ right: 10 }}>
				x
			</button>

			<ul>
				<li>
					<span><FaHome /></span>
					<NavLink className="card-link" aria-current="page" to="/">Home</NavLink>
				</li>
				<li>
					<span><FaYoutube /></span>
					<NavLink className="card-link" aria-current="page" to="/">Movies</NavLink>
				</li>
				<li>
					<span><FaProductHunt /></span>
					<NavLink className="card-link" aria-current="page" to="/">About</NavLink>
				</li>
			</ul>
		</div>
	)
}
export default Burger;
