import { Link, useLocation } from "react-router-dom";

export default function Home({ trends }) {
	const location = useLocation();


	return (
		<div className="content-custom">
			<h2>Trending today</h2>
			<div>
				{trends.map(({ id, title, poster_path }) => (
					<div class="card-group container" key={id} >
						<div className="row">
							<div className="col-15">
								<div class="card w-50">
									<img src={`https://image.tmdb.org/t/p/w200${poster_path}`} class="card-img-top" alt="" />
									<div class="card-body">
										<h5 class="card-title">{title}</h5>
										<Link to={`/movies/${id}`} state={{ from: location }} class="btn btn-primary">More...</Link>
									</div>
								</div>
							</div>
						</div>
					</div>

				))}
			</div>
		</div>
	);
};
