import React, { useEffect } from "react";
import { Outlet } from "react-router";
import { fetchMovieID } from '../../api'

//import { InfoWrapper, Form, Button, Text } from "./Searchbar.styled";
export default function Movie({ movieID }) {
	useEffect(() => {
		const url = `movie/${movieID}`
		async function fetchMovie() {
			try {
				//setIsLoading(true);
				const data = await fetchMovieID(url);
				const Movie = data.results.map(({ id, poster_path, title, release_date, vote_average, overview, genres
				}) => {
					return {
						id,
						poster_path,
						title,
						release_date,
						vote_average,
						overview,
						genres
					};
				});
			} catch (error) {
				console.log(error.massage)
			} finally {
				//setIsLoading(false);
			}
		}
		fetchMovie()
	}, [])

	return (
		<div>
			<button>Go back</button>
			<img src={poster_path} alt="" />
			<h2>{title}{release_date}</h2>
			<p>{vote_average}</p>
			<h3></h3>
			<p>{overview}</p>
			<h4></h4>
			<p>{genres.map(el => {
				return el.name
			})}</p>
		</div>

	)

}
