import Opinion from './Ecspresso/Opinion';
import { Recipe } from './Recipe/Recipe';
import recipes from '../recepies.json';
import Statistics from './Statistics/Statistics';
import React, { Component } from 'react';
import FeedbackOptions from './FeedbackOptions/FeedbackOptions';
import Section from './Section/Section';


class App extends Component {
	state = {
		good: 0,
		neutral: 0,
		bad: 0,
		total: 0,
		feedback: 100,
	}
	handleAmount = (e) => {
		let el = e.target.innerHTML.toLowerCase();
		console.log(el)
		this.setState((prevState) => ({
			[el]: prevState[el] + 1,
		}))

	}
	countTotalFeedback = () => {
		this.setState({
			total: this.state.good + this.state.neutral + this.state.bad,
		})
	}
	countPositiveFeedbackPercentage = () => {

	}

	render() {
		const { good, neutral, bad, total, feedback } = this.state

		return (
			<>
				<Section title="Please leave feetback">
					<FeedbackOptions
						// options={ }
						onLeaveFeedback={this.handleAmount}
					/>
				</Section>
				<Section title="Statistic">

				</Section>
				<Statistics
					good={good}
					neutral={neutral}
					bad={bad}
					total={total}
					feedback={feedback}
				/>



				<Opinion />
				{
					recipes.map((recipe, idx) => (
						<Recipe key={idx} recipe={recipe} />
					))
				}
			</>
		);
	}
};
export default App;
