import styled from 'styled-components';

export const Input = styled.input`

border-radius: 4px;
border: 3px solid #2196f3;
margin-right: 20px;
font-size: 18px;
width:30%;
outline: transparent;
:hover{
	background-color: #f4f8fa;
}
`;

export const Button = styled.button`
display: inline-block;
border-radius: 4px;
font-size: 18px;
background-color: #f4f8fa;
	border: 3px solid #2196f3;
	cursor: pointer;
	outline: none;
	:hover {opacity: 1}
`;
