import { useOutletContext } from "react-router-dom";


export default function Movies() {

	const [foundMovies] = useOutletContext();
	const location = useLocation();

	return (
		<div className="content-custom ">


			{/* <div className="card-group row row-cols-1 row-cols-sm-2 row-cols-md-3 row-cols-lg-4 g-4">
				{trends.map(({ id, title, poster_path }) => (

					<div className="col">
						<div class="card h-100" key={id}>
							<div className="card-body h-80">
								<img src={`https://image.tmdb.org/t/p/w200${poster_path}`} class="card-img-top " alt="" />
							</div>

							<div class="card-body ">
								<h5 class="card-title">{title}</h5>
								<Link to={`/movies/${id}`} state={{ from: location }} class="btn btn-primary">More...</Link>
							</div>
						</div>
					</div>


				))}
			</div> */}




			<div>
				{foundMovies.map(({ id, title }) => (
					<ul key={id}>
						<Link to={`/movies/${id}`} state={{ from: location }}>
							<li>{title}</li>
						</Link>
					</ul>
				))}
			</div>
		</div>
	)
}
