import { FaHome, FaYoutube, FaProductHunt } from 'react-icons/fa';
import { NavLink } from "react-router-dom";
import "./Basic.css";


export const Burger = ({ visibility, handleClick }) => {

	return (
		<div className={visibility ? "wrapper active" : "wrapper"}>
			<button className="dropdown" onClick={handleClick} >
				x
			</button>
			<ul className="list-group">
				<NavLink className="d-inline-flex p-2" aria-current="page" to="/"><span className='m-3'><FaHome /></span><li>Home</li></NavLink>

				<NavLink className="d-inline-flex p-2" aria-current="page" to="/movies"><span><FaYoutube /></span><li>Movies</li></NavLink>

				<NavLink className="d-inline-flex p-2" aria-current="page" to="/about"><span><FaProductHunt /></span><li>About</li></NavLink>

			</ul>
		</div>
	)
}
export default Burger;
