import { Border, Image } from "./ImageGallaryItem.styled";
import OnlyScroll from 'only-scrollbar';


export const ImageGallaryItem = ({ id, webformatURL, onSwitchModal, onModal }) => {
	const scroll = new OnlyScroll(<Border />, {
		damping: 0.8,
		eventContainer: window
	});
	return (
		<Border scroll >
			<Image src={webformatURL} alt={id} onClick={() => { onSwitchModal(); onModal() }} />
		</Border >
	)
}
