import React, { Component } from 'react';
import { Button, Text, Wrapper, StatisticsItem, StatisticsList, StatisticsName, StatisticsAmount } from './Opinion.styled'

class Opinion extends Component {
	state = {
		good: 0,
		neutral: 0,
		bad: 0,
	}

	handleAmount = (e) => {
		let name = e.target.innerHTML;
		console.log(name)
		this.setState(prevState => ({
			[name]: prevState[name] + 1,
		}))
	}
	render() {

		const { good, neutral, bad } = this.state
		console.log(typeof (this.state[0]))
		return (
			<Wrapper>
				<Text> Please leave feetback</Text>

				<Button onClick={(e) => this.handleAmount(e)}>Good</Button>
				<Button onClick={(e) => this.handleAmount(e)}>Neutral</Button>
				<Button onClick={(e) => this.handleAmount(e)}>Bad</Button>

				<Text>Statistics</Text>
				<StatisticsList>
					<StatisticsItem><StatisticsName>Good</StatisticsName><StatisticsAmount>{good}</StatisticsAmount></StatisticsItem>
					<StatisticsItem><StatisticsName>Neutral</StatisticsName><StatisticsAmount>{neutral}</StatisticsAmount></StatisticsItem>
					<StatisticsItem><StatisticsName>Bad</StatisticsName><StatisticsAmount>{bad}</StatisticsAmount></StatisticsItem>
				</StatisticsList>
			</Wrapper >
		);
	}
}
export default Opinion;
