import { useState, useEffect } from 'react';
//import Searchbar from "./Searchbar/Searchbar";
import { fetchTrendingToday, fetchSearchMovies } from 'api';
//import { Loader } from "../components/Loader/Loader";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import { Route, Routes } from "react-router-dom";
import { About } from "../pages/About";
import { Home } from "../pages/Home";
import { ProductDetails } from "../pages/ProductDetails";
import { Products } from "../pages/Products";
import { Container, Header, Logo, Link } from "./App.styled";
import Movies from "../pages/Movies"

export default function App() {
  const [trends, setTrends] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [query, setQuery] = useState('');
  const [foundMovies, setFoundMovies] = useState([])

  const url = {
    trending: 'trending/movie/day',
    search: 'search / movie'
    // / movie / { movie_id }
    // / movie / { movie_id } / credits
    // / movie / { movie_id } / reviews
  }


  useEffect(() => {
    // if (search === "") {

    //   return;
    // }
    async function fetchTrending() {
      // media_type: movie,
      //   time_window: day,

      try {
        setIsLoading(true);
        const data = await fetchTrendingToday(url.trending);
        const arrTrends = data.results.map(({ id, title, backdrop_path, poster_path
        }) => {
          return {
            id,
            title,
            backdrop_path,
            poster_path,
          };
        });

        if (arrTrends) {
          setTrends(arrTrends);
        }


        // if (imgs.length) {
        //   setImages(images => [...images, ...imgs]);
        //   setisBtn(true);
        // }
        // if (imgs.length === 0) {
        //   toast('–ü–æ –≤–∞—à–µ–º—É –∑–∞–ø—Ä–æ—Å—É –∫–∞—Ä—Ç–∏–Ω–æ–∫ –Ω–µ –Ω–∞–π–¥–µ–Ω–æ');
        //   setIsLoading(false);
        //   setisBtn(false);
        //   return
        // }
        // if (imgs.length < 12) {
        //   setIsLoading(false);
        //   toast('–ö–∞—Ä—Ç–∏–Ω–æ–∫ –±–æ–ª—å—à–µ –Ω–µ—Ç');
        //   setisBtn(false);
        //   return
        // }
        // if (page === 1) {
        //   toast(` –ú—ã –Ω–∞—à–ª–∏  ${data.totalHits} –∫–∞—Ä—Ç–∏–Ω–æ–∫.`);
        // }

      } catch (error) {
        toast(error.massage)
      } finally {
        setIsLoading(false);
      }
    }
    fetchTrending()

  }, [])

  useEffect(() => {
    if (query === "") {
      return;
    }
    async function fetchSearchMovies() {
      try {
        setIsLoading(true);
        const data = await fetchSearchMovies(url.search, query);
        const foundMovies = data.results.map(({ id, title, backdrop_path, poster_path
        }) => {
          return {
            id,
            title,
            backdrop_path,
            poster_path,
          };
        });

        if (foundMovies) {
          setFoundMovies(foundMovies);
        }
      } catch (error) {
        toast(error.massage)
      } finally {
        setIsLoading(false);
      }
    }
    fetchSearchMovies()

  }, [query, url.search])
  console.log(trends)
  // const toggleModal = () => {
  //   setShowModal(!showModal);
  // }

  const getVisiblImages = (value) => {
    if (value === "") {
      // setImages([]);
      // setPage(1);
      //toast('–í–ø–∏—à–∏—Ç–µ –∑–Ω–∞—á–µ–Ω–∏–µ –ø–æ–∏—Å–∫–∞')
      return;
    }
    // setSearch(value);
    // setImages([]);
    // setPage(1);

  }

  // const loadMore = () => {
  //   setPage(prevPage => prevPage + 1);
  // }


  return (
    // <Wrapper>
    //   <ToastContainer autoClose={1000} />
    //   <Searchbar onSearch={getVisiblImages} />
    //   {isLoading && <Loader />}
    // </Wrapper>
    <Container>
      <ToastContainer autoClose={1000} />
      <Header>
        {/* <Logo>
          <span role="img" aria-label="computer icon">
            üíª
          </span>{" "}
          GoMerch Store
        </Logo> */}
        <nav>
          <Link to="/" end>
            Home
          </Link>
          <Link to="/movies">Movies</Link>
          <Link to="/about">About</Link>
          <Link to="/products">Products</Link>

        </nav>
      </Header>
      <Routes>
        <Route path="/" element={<Home trends={trends} />} />
        <Route path="/movies" element={<Movies onSubmit={trends} />} />
        <Route path="/about" element={<About />} />
        <Route path="/products" element={<Products />} />
        <Route path="/products/:id" element={<ProductDetails />} />
      </Routes>
    </Container>
  );
}
