import { useState, useEffect } from 'react';
import { fetchTrendingToday, fetchSearchMovies } from 'api';
//import { Loader } from "../components/Loader/Loader";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import { Route, Routes } from "react-router-dom";
import { Home } from "../pages/Home";
import Movies from '../pages/Movies';
import Movie from '../pages/Movie';
import { Cast } from './Cast/Cast';
import { Reviews } from './Reviews/Reviews';
import { SharedLayout } from './SharedLayout/SharedLayout';


export default function App() {
  const [trends, setTrends] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [query, setQuery] = useState('');
  const [foundMovies, setFoundMovies] = useState([])

  useEffect(() => {
    async function fetchTrending() {
      const url = 'trending/movie/day'
      try {
        setIsLoading(true);
        const data = await fetchTrendingToday(url);
        const trendsMovies = data.results.map(({ id, title
        }) => {
          return { id, title };
        });
        if (trendsMovies) {
          setTrends(trendsMovies);
        }
      } catch (error) {
        toast(error.massage)
      } finally {
        setIsLoading(false);
      }
    }
    fetchTrending()
  }, [])

  useEffect(() => {
    if (query === "") {
      return;
    }
    async function fetchSearch() {
      const url = 'search/movie'
      try {
        setIsLoading(true);
        const data = await fetchSearchMovies(url, query);
        const foundMovies = data.results.map(({ id, title, backdrop_path, poster_path
        }) => {
          return {
            id,
            title,
            backdrop_path,
            poster_path,
          };
        });

        if (foundMovies) {
          setFoundMovies(foundMovies);
        }
      } catch (error) {
        toast(error.massage)
      } finally {
        setIsLoading(false);
      }
    }
    fetchSearch()

  }, [query])

  // const toggleModal = () => {
  //   setShowModal(!showModal);
  // }

  const getQueryValue = (value) => {
    if (value === "") {
      setQuery(value)
      toast('Впишите значение поиска')
      return;
    }
    setQuery(value);
    //setFoundMovies([])
  }
  return (
    <>
      <ToastContainer />
      <Routes>
        <Route path="/" element={<SharedLayout />}>
          <Route index element={<Home trends={trends} />} />
          <Route path="/movies" element={<Movies onSearch={getQueryValue} foundMovies={foundMovies} />} />
          <Route path="/movies/:movieID" element={<Movie />} >
            <Route path="cast" element={<Cast />} />
            <Route path="reviews" element={<Reviews />} />
          </Route>
          <Route path="*" element={<div>Not found</div>} />
        </Route>
      </Routes>
    </>
  );
}
