import { useState } from "react";

import "./Basic.css";





export const Burger = ({ open }) => {
	const [visibility, setVisibility] = useState(false)

	const handleClick = () => {
		setVisibility(prevvisibility => !prevvisibility)
	}

	const openAnimation = {
		x: 0,
		transition: {
			duration: 200
		}
	};
	return (
		<div
			className="wrapper"
			open={visibility}
		>
			<button onClick={handleClick} style={{ right: 10 }}>
				x
			</button>

			<ul>
				<li>
					<a href="">Home</a>
					<span></span>
				</li>
				<li>
					<a href="">Movies</a>
					<span></span>
				</li>
				<li>
					<a href="">About</a>
					<span></span>
				</li>
			</ul>
		</div>
	)
}
export default Burger;
