import { Link, useLocation } from "react-router-dom";

export default function Home({ trends }) {
	const location = useLocation();
	return (
		<div class="container">
			<div>
				<h2>Trending today</h2>
				<div>
					{trends.map(({ id, title }) => (
						<ul key={id}>
							<Link to={`/movies/${id}`} state={{ from: location }}>
								<li>{title}</li>
							</Link>
						</ul>
					))}
				</div>
			</div>

			<footer className="d-flex flex-wrap justify-content-between align-items-center py-3 my-4 border-top">
				<div className="col-md-4 d-flex align-items-center">
					<a href="/" className="mb-3 me-2 mb-md-0 text-muted text-decoration-none lh-1">
						<svg className="bi" width="30" height="24"><link href="bootstrap.com"></link></svg>
					</a>
					<span className="mb-3 mb-md-0 text-muted">Â© 2022 Company, Inc</span>
				</div>

				<ul className="nav col-md-4 justify-content-end list-unstyled d-flex">
					<li className="ms-3"><link className="text-muted" to="/"><svg className="bi" width="24" height="24"><link href="twitter.com"></link></svg></link></li>
					<li className="ms-3"><link className="text-muted" to="/"><svg className="bi" width="24" height="24"><link href="instagram.com"></link></svg></link></li>
					<li className="ms-3"><link className="text-muted" to="/"><svg className="bi" width="24" height="24"><link href="facebook.com"></link></svg></link></li>
				</ul>
			</footer>

		</div>


	);
};
