import { Gallery } from "./ImageGallary.styled";
import { ImageGallaryItem } from "../ImageGallaryItem/ImageGallaryItem";
import { useEffect, useRef } from "react";

export const ImageGallary = ({ images, onSwitchModal, onModal }) => {
	const img = (images) => {
		const imgEndRef = useRef(null)
		const scrollToButtom = () => {
			imgEndRef.current.scrollIntoView({ behavoir: 'smooth' })
		}

		useEffect(() => {
			scrollToButtom()
		}, [images])
	}
	return (
		<Gallery>
			{images.map(el => (
				<ImageGallaryItem
					key={el.id}
					id={el.id}
					webformatURL={el.webformatURL}
					largeImageURL={el.largeImageURL}
					onSwitchModal={() => onSwitchModal(el.largeImageURL)}
					onModal={onModal}
				/>
			))}
		</Gallery>
	)
}
