import { useState, useEffect } from 'react';
import Searchbar from "./Searchbar/Searchbar";
import { fetchImages } from 'api';
import { ImageGallary } from './ImageGallery/ImageGallary';
import { Wrapper } from "./App.styled";
import { ButtonLoadMore } from '../components/Button/Button';
import { Loader } from "../components/Loader/Loader";
import Modal from "./Modal/Modal";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';

export default function App() {
  const [images, setImages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [search, setSearch] = useState('');
  const [isBtn, setisBtn] = useState(false);
  const [page, setPage] = useState(1);
  const [showModal, setShowModal] = useState(false);
  const [activImg, setActivImg] = useState(null);


  useEffect(() => {
    if (search === "") {
      setisBtn(false);
      return;
    }
    async function addMoreImages() {
      try {
        setIsLoading(true);
        const data = await fetchImages(search, page);
        const imgs = data.hits.map(({ id, webformatURL, largeImageURL, tags }) => {
          return {
            id,
            webformatURL,
            largeImageURL,
            tags,
          };
        });
        if (imgs.length) {
          setImages(images => [...images, ...imgs]);
          setisBtn(true);
        }
        if (imgs.length === 0) {
          toast('По вашему запросу картинок не найдено');
          setIsLoading(false);
          setisBtn(false);
          return
        }
        if (imgs.length < 12) {
          setIsLoading(false);
          toast('Картинок больше нет');
          setisBtn(false);
          return
        }
        if (page === 1) {
          toast(` Мы нашли  ${data.totalHits} картинок.`);
        }
        setIsLoading(false);
      } catch (error) {
        toast(error.massage)
      }
    }
    addMoreImages()
    scrollOnLoadButton()
  }, [search, page])

  const toggleModal = () => {
    setShowModal(!showModal);
  }

  const getVisiblImages = (value) => {
    if (value === "") {
      setImages([]);
      setPage(1);
      toast('Впишите значение поиска')
      return;
    }
    setSearch(value);
    setImages([]);
    setPage(1);

  }

  const loadMore = () => {
    setPage(prevPage => prevPage + 1);
  }
  const scrollOnLoadButton = () => {
    window.scrollTo({
      top: document.documentElement.scrollHeight,
      behavior: 'smooth',
    });
  };

  return (
    <Wrapper>
      <ToastContainer autoClose={1000} />
      <Searchbar onSearch={getVisiblImages} />
      {showModal && (
        <Modal onClose={toggleModal}>
          <img src={activImg} alt="" />
        </Modal>
      )}
      {isLoading ? <Loader /> : <ImageGallary images={images} onSwitchModal={setActivImg} onModal={toggleModal} />}
      {isBtn && <ButtonLoadMore onLoadMore={loadMore} onScroll={scrollOnLoadButton}>Load more</ButtonLoadMore>}
    </Wrapper>
  );
}
