import React from "react";


class App extends React.Component {
  state = {
    count: 0,
    amount: false,
  }
  handlePlus = () => {
    this.setState((state) => ({ count: state.count + 1 }))
  }
  render() {
    return (
      <div className="App">
        <h1>привет{this.state.count}</h1>
        <button onClick={this.handlePlus}>+</button>
        <button>-</button>
      </div>
    );
  }

}

export default App;
