import { Formik, Form, Field, ErrorMessage } from "formik";
import * as yup from 'yup';


//оборачиваем всю форму в Formik
//заменим Form на form
//Field заменяет input
//onSubmit получает value и action, из последних достаем функции типа resetForm


const schema = yup.object().shape({
	login: yup.string().required(),
	password: yup.number().min(6).max(16).required(),
	// email: yup.string().email(),
	// website: yup.string().url(),
	// createdOn: yup.date().default(function () {
	//   return new Date();
	// }),
});

const initialValues = {
	login: "",
	password: "",
}

export const LoginForm = () => {
	const handleSubmit = (values, { resetForm }) => {
		resetForm()
	}
	return (
		<Formik
			initialValues={initialValues}
			onSubmit={handleSubmit}
			validationSchema={schema}
		>
			<Form autoComplete='off'>
				<label htmlFor="login">
					Login
					<Field type="text" name="login" />
					<ErrorMessage name="login" component='div' />
				</label>
				<label htmlFor="password">
					Password
					<Field type="password" name="password" component='div' />
					<ErrorMessage name="password" />
				</label>
				<button type="submit">Send</button>
			</Form>
		</Formik>
	)
}
