import { Component } from 'react';
import { InfoWrapper, Form, Button, Text } from "./Searchbar.styled";
import { ReactComponent as Loop } from "../Icons/iconloop.svg";

export default class Searchbar extends Component {
	state = {
		value: '',
		isLoading: false,
	}
	handleValue = e => {
		this.setState({
			value: e.currentTarget.value
		})
	}
	render() {
		return (
			<InfoWrapper >
				<Form  >
					<Button type="submit" onClick={this.props.onSearchLoop(this.state.value)}>
						<Loop />
					</Button>

					<Text
						className="input"
						type="text"
						autocomplete="off"
						placeholder="Search images and photos"
						value={this.value}
						onChange={this.handleValue}
					/>
				</Form>
			</InfoWrapper >
		)
	}

}
