import React from "react";
import { Searchbar } from "./Searchbar/Searchbar";
import { Component } from 'react';
import { fetchImages } from 'api';
import { ImageGallary } from './ImageGallery/ImageGallary';
import { Wrapper } from "./App.styled";
import { ButtonLoadMore } from '../components/Button/Button';
import { Loader } from "../components/Loader/Loader";
import Modal from "./Modal/Modal";



export default class App extends Component {
  state = {
    images: [],
    error: '',
    isLoading: true,
    showModal: false,
    filter: '',
  };

  async componentDidMount() {
    try {
      this.setState({ isLoading: true });
      const images = await fetchImages();
      this.setState({
        images: images.hits,
        isLoading: false,
      });
    } catch (error) {
      this.setState({
        error:
          '–ú—ã –Ω–µ —Å–º–æ–≥–ª–∏ –∑–∞–≥—Ä—É–∑–∏—Ç—å –∫–∞—Ä—Ç–∏–Ω–∫–∏, –ø–æ–∂–∞–ª—É–π—Å—Ç–∞ –ø–µ—Ä–µ–∑–∞–≥—Ä—É–∑–∏—Ç–µ —Å—Ç—Ä–∞–Ω–∏—Ü—É —á—Ç–æ–±—ã –ø–æ–ø—Ä–æ–±–æ–≤–∞—Ç—å –µ—â–µ —Ä–∞–∑ ü•π',
      });
    }
  }

  toggleModal = () => {
    this.setState(({ showModal }) => ({
      showModal: !showModal,
    }))
  }

  changeFilter = e => {
    this.setState({
      filter: e.currentTarget.value
    })
  }

  getVisiblImages = (event) => {
    event.preventDefault()
    const vizible = this.state.images.filter(el =>
      el.user.toLowerCase().includes(this.state.filter.toLowerCase())
    )
    return vizible
  }

  // selectBreed = async (breedId) => {
  //   try {
  //     const dog = await fetchDogByBreed(breedId);
  //     this.setState({ dog: dog });
  //   } catch (e) {
