  //     });
  //   }
  // };

  render() {

    return (
      <Wrapper>
        <Searchbar value={this.state.filter} onChange={this.changeFilter} onSearchLoop={this.getVisiblImages} />
        {this.state.showModal && (
          <Modal onClose={this.toggleModal}>
            ghghghghghgghgghgh
          </Modal>
        )}
        {this.state.isLoading ? <Loader /> : <ImageGallary images={this.state.images} />}
        <ButtonLoadMore />
        <button type='button' onClick={this.toggleModal}>modal</button>
      </Wrapper>
    );
  }
}
