import { useState, useEffect } from "react";
import { Link, useLocation, useSearchParams } from "react-router-dom";
import { Input, Button } from "./Movies.styled";
import { fetchSearchMovies } from '../../api'
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';

export default function Movies() {
	const [query, setQuery] = useState('');
	const [foundMovies, setFoundMovies] = useState([]);
	const [searchParams, setSearchParams] = useSearchParams();
	const valueParam = searchParams.get('search') ?? '';
	const location = useLocation();


	const handleSubmit = event => {
		event.preventDefault();
		const form = event.currentTarget;
		const value = form.elements.searchValue.value;

		if (value === "") {
			setQuery(value)
			toast('Впишите значение поиска')
			return;
		}
		setQuery(value);
		form.reset();
	}
	useEffect(() => {
		if (query === "") {
			return;
		}
		async function fetchSearch() {
			const url = 'search/movie'
			try {
				const data = await fetchSearchMovies(url, query);
				const foundMovies = data.results.map(({ id, title
				}) => {
					return {
						id,
						title,
					};
				});
				if (foundMovies) {
					setFoundMovies(foundMovies);
				}
			} catch (error) {
				toast(error.massage)
			}
		}
		fetchSearch()
	}, [query])
	const handleValue = value => {
		setSearchParams(value !== '' ? { search: value } : {});
	}


	return (
		<>
			<ToastContainer />
			<form onSubmit={handleSubmit}>
				<Input
					type="text"
					name="searchValue"
					placeholder="Search video"
					value={searchParams}
					onChange={handleValue}
				/>
				<Button type="submit" >
					Search
				</Button>
			</form>
			<div>
				{foundMovies.map(({ id, title }) => (
					<ul key={id}>
						<Link to={`/movies/${id}`} state={{ from: location }}>
							<li>{title}</li>
						</Link>
					</ul>
				))}
			</div>
		</>
	)
}
