import { useOutletContext } from "react-router-dom";
import { useState, useEffect } from 'react';
import { fetchMovieIDReviews } from 'api';


export const Reviews = () => {
	const [author, setAuthor] = useState('')
	const [content, setContent] = useState('')


	const { movieID } = useOutletContext();

	useEffect(() => {
		const url = `movie/${movieID}/reviews`
		async function fetchMovie() {
			try {
				const reviews = await fetchMovieIDReviews(url);
				console.log(reviews)
				if (reviews.results) {
					{
						reviews.results.map(({ id, title }) => (
							<ul key={id}>
								<Link to={`/movies/${id}`}>
									<li>{title}</li>
								</Link>
							</ul>
						))
					}

					setAuthor(reviews.results[0].author)
					setContent(reviews.results[0].content)
					return;
				}
				return
			} catch (error) {
				console.log(error)
			}
		}
		fetchMovie()
	}, [movieID])

	return (
		<section>
			{true ?
				(<div>
					<h2>Author: {author}</h2>
					<p>
						{content}
					</p>
				</div>)
				:
				(<p>Not reviews</p>)
			}
		</section>
	);
};
