import { useState, useEffect } from 'react';
//import Searchbar from "./Searchbar/Searchbar";
import { fetchImages } from 'api';
//import { Loader } from "../components/Loader/Loader";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import { Route, Routes } from "react-router-dom";
import { About } from "../pages/About";
import { Home } from "../pages/Home";
import { ProductDetails } from "../pages/ProductDetails";
import { Products } from "../pages/Products";
import { Container, Header, Logo, Link } from "./App.styled";

export default function App() {
  const [trends, setTrends] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  // const url = {
  //   /trending/{ media_type } /{ time_window }
  // 	/ search / movie(language, query)
  // 	/ movie / { movie_id }
  // 	/ movie / { movie_id } / credits
  // 	/ movie / { movie_id } / reviews
  // }

  const url = 'trending/movie/day'


  useEffect(() => {
    // if (search === "") {

    //   return;
    // }
    async function addMoreImages() {
      // media_type: movie,
      //   time_window: day,

      try {
        setIsLoading(true);
        const data = await fetchImages(url);
        const arrTrends = data.results.map(({ id, title, backdrop_path, poster_path
        }) => {
          return {
            id,
            title,
            backdrop_path,
            poster_path,
          };
        });

        if (arrTrends) {
          setTrends(arrTrends);
        }


        // if (imgs.length) {
        //   setImages(images => [...images, ...imgs]);
        //   setisBtn(true);
        // }
        // if (imgs.length === 0) {
        //   toast('По вашему запросу картинок не найдено');
        //   setIsLoading(false);
        //   setisBtn(false);
        //   return
        // }
        // if (imgs.length < 12) {
        //   setIsLoading(false);
        //   toast('Картинок больше нет');
        //   setisBtn(false);
        //   return
        // }
        // if (page === 1) {
        //   toast(` Мы нашли  ${data.totalHits} картинок.`);
        // }

      } catch (error) {
        toast(error.massage)
      } finally {
        setIsLoading(false);
      }
    }
    addMoreImages()
    console.log(trends)
  }, [])

  // const toggleModal = () => {
  //   setShowModal(!showModal);
  // }

  const getVisiblImages = (value) => {
    if (value === "") {
      // setImages([]);
      // setPage(1);
      //toast('Впишите значение поиска')
      return;
    }
    // setSearch(value);
    // setImages([]);
    // setPage(1);

  }

  // const loadMore = () => {
  //   setPage(prevPage => prevPage + 1);
  // }


  return (
    // <Wrapper>
    //   <ToastContainer autoClose={1000} />
    //   <Searchbar onSearch={getVisiblImages} />
    //   {isLoading && <Loader />}
    // </Wrapper>
    <Container>
      <ToastContainer autoClose={1000} />
      <Header>
        <Logo>
          <span role="img" aria-label="computer icon">
            💻
          </span>{" "}
          GoMerch Store
        </Logo>
        <nav>
          <Link to="/" end>
            Home
          </Link>
          <Link to="/about">About</Link>
          <Link to="/products">Products</Link>
        </nav>
      </Header>
      <Routes>
        <Route path="/" element={<Home trends={trends} />} />
        <Route path="/about" element={<About />} />
        <Route path="/products" element={<Products />} />
        <Route path="/products/:id" element={<ProductDetails />} />
      </Routes>
    </Container>
  );
}
