import React from "react";
import { Component } from 'react';
import { InfoWrapper, Form, Button, Text } from "./Searchbar.styled";
import { ReactComponent as Loop } from "../Icons/iconloop.svg";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';

export default class Searchbar extends Component {
	state = {
		value: '',
	}

	handleValue = e => {
		e.preventDefault()
		this.setState({
			value: e.currentTarget.value
		})
	}




	render() {
		return (
			<InfoWrapper >
				<ToastContainer />

				<Button type="submit" onClick={() => this.props.onSearch(this.state.value)}  >
					<Loop />
				</Button>

				<Text
					className="input"
					type="text"
					autocomplete="off"
					placeholder="Search images and photos"
					value={this.value}
					onChange={this.handleValue}
				/>

			</InfoWrapper >
		)
	}

}
