import React from "react";


export default function Movies({ onSearch, foundMovies }) {
	const handleSubmit = event => {
		event.preventDefault();
		const form = event.currentTarget;
		const value = form.elements.searchValue.value;
		onSearch(value);
		form.reset();
	}
	return (
		<form onSubmit={handleSubmit}>

			<input
				className="input"
				type="text"
				name="searchValue"
				autocomplete="off"
				placeholder="Search images and photos"
			/>
			<button type="submit" >
				Search
			</button>
			<div>
				{foundMovies.map((product) => (
					<ul key={product.id}>
						<Link to={`${product.id}`}>
							<li>{product.title}</li>
						</Link>
					</ul>
				))}
			</div>
		</form>
	)
}
