import styled from 'styled-components';

export const Input = styled.input`

border-radius: 2px;
border: 3px solid #2196f3;
margin-right: 20px;
font-size: 20px;
width:30%;
outline: transparent;
:hover{
	background-color: #f4f8fa;
}
`;












export const Form = styled.form`
padding: 10px 15px;
border-radius: 10px;
border: 3px solid #2196f3;
margin-right: 20px;
font-size: 20px;
width:30%;
outline: transparent;
:hover{
	background-color: #f4f8fa;
}
`;

export const Button = styled.button`
display: inline-block;
	width: 48px;
	height: 48px;
	border: 0;
	background-image:url('../Icons/iconloop.svg');
	background-size:40%;
	background-repeat:no-repeat;
	background-position:center;
	opacity: 0.6;
	transition: opacity 250ms cubic-bezier(0.4,0,0.2,1);
	cursor: pointer;
	outline: none;
	:hover {opacity: 1}
`;

export const Label = styled.span`
position: absolute;
	width: 1px;
	height: 1px;
	padding: 0;
	overflow: hidden;
	clip: rect(0, 0, 0, 0);
	white-space: nowrap;
	clip-path: inset(50%);
	border: 0;
`;
