import styled from "styled-components"

export const Button = styled.button`
background-color: #4CAF50; 
    border: none;
    color: white;
    padding: 10px 32px;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    font-size: 16px;
	 :not(:last-child) {
		margin-right: 5px
	}
`;

export const Text = styled.h2`
padding: 20px 0px
`;



export const Wrapper = styled.div`
padding-left: 10px
`;
