import React from "react";
import { Searchbar } from "./Searchbar/Searchbar";
import { Component } from 'react';
import { fetchImages } from 'api';
import { ImageGallary } from './ImageGallery/ImageGallary';
import { Wrapper } from "./App.styled";
import { ButtonLoadMore } from '../components/Button/Button';
import { Loader } from "../components/Loader/Loader";
import Modal from "./Modal/Modal";
import { ToastContainer, toast } from "react-toastify";


export default class App extends Component {
  state = {
    images: [],
    error: '',
    isLoading: false,
    showModal: false,
    search: '',
    page: 1,
    activImg: '',
  };

  async componentDidMount() {
    try {
      this.setState({ isLoading: true });
      const images = await fetchImages();
      this.setState({
        images: images.hits,
        isLoading: false,
      });
    } catch (error) {
      this.setState({
        error:
          '–ú—ã –Ω–µ —Å–º–æ–≥–ª–∏ –∑–∞–≥—Ä—É–∑–∏—Ç—å –∫–∞—Ä—Ç–∏–Ω–∫–∏, –ø–æ–∂–∞–ª—É–π—Å—Ç–∞ –ø–µ—Ä–µ–∑–∞–≥—Ä—É–∑–∏—Ç–µ —Å—Ç—Ä–∞–Ω–∏—Ü—É —á—Ç–æ–±—ã –ø–æ–ø—Ä–æ–±–æ–≤–∞—Ç—å –µ—â–µ —Ä–∞–∑ ü•π',
      });
    }
  }
  async componentDidUpdate(_, prevState) {
    const { page, search } = this.state
    if (prevState.page !== page ||
      prevState.state.search !== search) {
      console.log(page)
      const nextImages = await fetchImages(page, search);
      this.setState(prevState => ({
        images: [...prevState.images, ...nextImages.hits],
        page: this.state.page + 1,
        isLoading: false,
      }))
    }

  }


  toggleModal = () => {
    this.setState(({ showModal }) => ({
      showModal: !showModal,
    }))
  }

  changeSearch = e => {
    this.setState({
      search: e.currentTarget.value
    })
  }

  getVisiblImages = (event) => {
    event.preventDefault()
    if (this.state.search.trim() === "") {
      toast("–í–≤–µ–¥–∏—Ç–µ –∑–Ω–∞—á–µ–Ω–∏–µ –ø–æ–∏—Å–∫–∞")
      return;
    }
  }

  handleModal = (url) => {
    console.log(url)
    this.setState({
      activImg: url
    })
  }
  loadMore = () => {
    this.setState(prevState => ({
      page: prevState.page + 1
    }))
  }


  // handleLoadMore = async () => {
  //   try {
  //     this.setState({ isLoading: true });
  //     console.log(this.state.page)
  //     const newImages = await fetchImages(this.state.page + 1);
  //     console.log(newImages)
  //     this.setState(prevState => ({
  //       images: [...prevState.images, ...newImages.hits],
  //       page: this.state.page + 1,
  //       isLoading: false,
  //     }))
  //   } catch (error) {
  //     this.setState({
  //       error:
  //         '–ú—ã –Ω–µ —Å–º–æ–≥–ª–∏ –∑–∞–≥—Ä—É–∑–∏—Ç—å –∫–∞—Ä—Ç–∏–Ω–∫–∏, –ø–æ–∂–∞–ª—É–π—Å—Ç–∞ –ø–µ—Ä–µ–∑–∞–≥—Ä—É–∑–∏—Ç–µ —Å—Ç—Ä–∞–Ω–∏—Ü—É —á—Ç–æ–±—ã –ø–æ–ø—Ä–æ–±–æ–≤–∞—Ç—å –µ—â–µ —Ä–∞–∑ ü•π',
  //     });
  //   }
  // }

  render() {
    const { showModal, isLoading, error, images } = this.state;
    return (
      <Wrapper>
        <ToastContainer />
        {error && <p>{this.state.error}</p>}
        <Searchbar value={this.state.search} onChange={this.changeSearch} onSearchLoop={this.getVisiblImages} />
        {showModal && (
          <Modal onClose={this.toggleModal}>
            <img src={this.state.activImg} alt="name" />
          </Modal>
        )}
        {isLoading ? <Loader /> : <ImageGallary images={images} onSwitchModal={this.handleModal} onModal={this.toggleModal} />}
        <ButtonLoadMore onLoadMore={this.loadMore} />
      </Wrapper>
    );
  }


}
