import React from "react";
import { InfoWrapper, Form, Button, Text } from "./Searchbar.styled";
import { ReactComponent as Loop } from "../Icons/iconloop.svg";


export default function Searchbar({ onSearch }) {


	const handleSubmit = event => {
		event.preventDefault();
		const form = event.currentTarget;
		const value = form.elements.searchValue.value;
		onSearch(value);
		form.reset();
	}

	return (
		<InfoWrapper >
			<Form onSubmit={handleSubmit}>
				<Button type="submit"  >
					<Loop />
				</Button>
				<Text
					className="input"
					type="text"
					name="searchValue"
					autocomplete="off"
					placeholder="Search images and photos"
				/>
			</Form>
		</InfoWrapper >
	)

}
