import React from "react";
import Searchbar from "./Searchbar/Searchbar";
import { Component } from 'react';
import { fetchImages } from 'api';
import { ImageGallary } from './ImageGallery/ImageGallary';
import { Wrapper } from "./App.styled";
import { ButtonLoadMore } from '../components/Button/Button';
import { Loader } from "../components/Loader/Loader";
import Modal from "./Modal/Modal";



export default class App extends Component {
  state = {
    images: [],
    error: '',
    isLoading: false,
    showModal: false,
    search: '',
    page: 1,
    activImg: '',
  };

  async componentDidMount() {
    try {
      this.setState({ isLoading: true });
      const images = await fetchImages();
      this.setState({
        images: images.hits,
        isLoading: false,
      });
    } catch (error) {
      this.setState({
        error:
          'ÐœÑ‹ Ð½Ðµ ÑÐ¼Ð¾Ð³Ð»Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ·Ð¸Ñ‚ÑŒ ÐºÐ°Ñ€Ñ‚Ð¸Ð½ÐºÐ¸, Ð¿Ð¾Ð¶Ð°Ð»ÑƒÐ¹ÑÑ‚Ð° Ð¿ÐµÑ€ÐµÐ·Ð°Ð³Ñ€ÑƒÐ·Ð¸Ñ‚Ðµ ÑÑ‚Ñ€Ð°Ð½Ð¸Ñ†Ñƒ Ñ‡Ñ‚Ð¾Ð±Ñ‹ Ð¿Ð¾Ð¿Ñ€Ð¾Ð±Ð¾Ð²Ð°Ñ‚ÑŒ ÐµÑ‰Ðµ Ñ€Ð°Ð· ðŸ¥¹',
      });
    }
  }

  async componentDidUpdate(_, prevState) {
    const { page, search } = this.state
    if (prevState.page !== page || prevState.search !== search
    ) {
      const nextImages = await fetchImages(search, page,);
      //Ð¼Ð¾Ð¶ÐµÐ¼ Ð´Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ ÐºÐ°Ñ€Ñ‚Ð¸Ð½ÐºÐ¸ Ð²Ð½Ð¸Ð·
      // this.setState(prevState => ({
      //   images: [...prevState.images, ...nextImages.hits],
      // }))
      this.setState({
        images: nextImages.hits,
      })
    }
  }
  //prevState.search !== search

  toggleModal = () => {
    this.setState(({ showModal }) => ({
      showModal: !showModal,
    }))
  }



  getVisiblImages = (value) => {
    this.setState({
      search: value
    })
  }

  handleModal = (url) => {
    this.setState({
      activImg: url
    })
  }
  loadMore = () => {
    this.setState(prevState => ({
      page: prevState.page + 1
    }))
  }

  render() {
    const { showModal, isLoading, error, images } = this.state;
    return (
      <Wrapper>

        {error && <p>{error}</p>}
        <Searchbar onSearch={this.getVisiblImages} />
        {showModal && (
          <Modal onClose={this.toggleModal}>
            <img src={this.state.activImg} alt="name" />
          </Modal>
        )}
        {isLoading ? <Loader /> : <ImageGallary images={images} onSwitchModal={this.handleModal} onModal={this.toggleModal} />}
        <ButtonLoadMore onLoadMore={this.loadMore} />
      </Wrapper>
    );
  }
}
